"""Unit tests of "5. Longest Palindromic Substring".

https://leetcode.com/problems/longest-palindromic-substring/
"""

import pytest

from pyleetcode.medium.longest_palindromic_substring import Solution


TEST_CASES = [
    ('babad', 'bab'),
    ('cbbd', 'bb'),
    ('cxedexy', 'xedex'),
    ('ccc', 'ccc'),
    ('ccd', 'cc'),
    ('ac', 'a'),
    ('xaabacxcabaaxcabaax', 'xaabacxcabaax'),
    ('ababababa', 'ababababa'),
    ('ababababababa', 'ababababababa'),
    ('',
     ''),
    ('',
     ''),
    ("",
     ""),
]  # noqa


@pytest.mark.parametrize('s, output', TEST_CASES)
def test(s, output):
    res = Solution().process(s)
    assert res == output
